// /components/ChatSection.js
import React, { useState } from "react";
import axios from "axios";

function ChatSection() {
  const [userInput, setUserInput] = useState("");
  const [chatResponse, setChatResponse] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  // Function to handle sending the chat to OpenAI and then saving it locally
  const handleChatSubmit = async () => {
    if (!userInput.trim()) return;
    setIsLoading(true);
    setChatResponse("");

    try {
      // First, call the OpenAI API for a response
      const response = await axios.post(
        "https://api.openai.com/v1/chat/completions",
        {
          model: "gpt-3.5-turbo",
          messages: [{ role: "user", content: userInput }],
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer YOUR_OPENAI_API_KEY_HERE`
          },
        }
      );

      // Extract the assistant's reply from the API response
      const assistantMessage = response.data.choices[0].message.content;
      setChatResponse(assistantMessage);

      // Post the user question and the assistant's response to your local backend
      await axios.post("http://localhost:5000/api/chats", {
        visitor_id: "visitor123", // Optional, can be dynamic based on your app
        question: userInput,
        response: assistantMessage,
      });
      

    } catch (error) {
      console.error(error);
      setChatResponse("Oops, something went wrong. Try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <section className="container my-5">
      <h2 className="text-center mb-4">Talk with ChatGPT</h2>
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="mb-3">
            <label htmlFor="chatInput" className="form-label">
              Ask me anything:
            </label>
            <input
              type="text"
              className="form-control"
              id="chatInput"
              placeholder="Type your question..."
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === "Enter") {
                  handleChatSubmit();
                }
              }}
            />
          </div>
          <button
            className="btn btn-accent"
            onClick={handleChatSubmit}
            disabled={isLoading}
          >
            {isLoading ? "Thinking..." : "Send"}
          </button>

          {/* Display the assistant's response */}
          {chatResponse && (
            <div className="mt-4 p-3 border rounded" style={{ backgroundColor: "#fff" }}>
              <strong>ChatGPT:</strong> {chatResponse}
            </div>
          )}
        </div>
      </div>
    </section>
  );
}

export default ChatSection;
